def center_crop(img, width):
	y, x = img.shape
	x_center = x/2.0
	y_center = y/2.0
	x_min = int(x_center - width/2.0)
	x_max = x_min + width
	y_min = int(y_center - width/2.0)
	y_max = y_min + width
	img_cropped = img[y_min:y_max, x_min: x_max]
	
	return img_cropped


def random_crop(img, width, height):
	x = random.randint(0, img.shape[1] - width)
	y = random.randint(0, img.shape[0] - height)

	img_cropped = img[y:y+height, x:x+width]

	return img_cropped

def image_minmax(img):
	img_minmax = ((img - np.min(img)) / (np.max(img) - np.min(img))).copy()
	img_minmax = (img_minmax * 255).astype(np.uint8)
		
	return img_minmax

class VFDataset(Dataset):
	def __init__(self, is_Train, args):
		self.args = args
		self.is_Train = is_Train
		# self.img_list,self.label_list = self._load_image_list()
		self.img_list,self.label_list = self._load_fracture_image_list()

		print("# of %s images : %d" % ('training' if is_Train else 'validation', len(self.img_list)))

	def __getitem__(self, index):
		img_path = self.img_list[index]

		# Load Image
		
		image = sitk.ReadImage(img_path)
		image = sitk.GetArrayFromImage(image)[0]

		image = image_minmax(image)
		# image = image/255.
		image = image.astype(np.float32)
		# image = random_crop(image, 500,500)
		image = center_crop(image, 500)

		# print(image.shape)
		# Load Class
		class_idx = self.label_list[index]
		if self.is_Train:
			image = self.augment_img(image)

		# image = image[None, ...].astype(np.float32)
		image = np.stack((image,)*3, axis=0).astype(np.float32)
		return image, class_idx

	def __len__(self):
		return len(self.img_list)


	def _load_image_list(self):
		vf_img_list = self.remove_invalid(sorted(glob(os.path.join(self.args.data_root, 'VF',"*",'*.dcm')))[:])
		nonvf_img_list = self.remove_invalid(sorted(glob(os.path.join(self.args.data_root, 'Non-VF', "*", '*.dcm')))[:])
		
		target_img_list = []
		# split_idx = int(len(vf_img_list)*self.args.train_val_ratio)
		split_idx = int(len(vf_img_list)*.6)
		split_idx_val = int(len(vf_img_list)*.2)
		target_img_list.extend(vf_img_list[:split_idx]) if self.is_Train else target_img_list.extend(vf_img_list[split_idx:split_idx+split_idx_val]) 
		vf_len = len(target_img_list)

		# split_idx = int(len(nonvf_img_list)*self.args.train_val_ratio)
		split_idx = int(len(nonvf_img_list)*.6)
		split_idx_val = int(len(nonvf_img_list)*.2)
		target_img_list.extend(nonvf_img_list[:split_idx]) if self.is_Train else target_img_list.extend(nonvf_img_list[split_idx:split_idx+split_idx_val]) 
		nonvf_len = len(target_img_list) -vf_len

		target_label_list = ([1] * vf_len)+([0] * nonvf_len)

		return target_img_list,target_label_list

	def _load_fracture_image_list(self):
		vf_img_list_fracture,vf_img_list_non = self.remove_invalid_fracture(sorted(glob(os.path.join(self.args.data_root, 'VF',"*",'*.dcm')))[:200])
		nonvf_img_list_fracture,nonvf_img_list_non = self.remove_invalid_fracture(sorted(glob(os.path.join(self.args.data_root, 'Non-VF', "*", '*.dcm')))[:200])
		
		vf_img_list = vf_img_list_fracture + nonvf_img_list_fracture
		nonvf_img_list = vf_img_list_non + nonvf_img_list_non

		target_img_list = []
		# split_idx = int(len(vf_img_list)*self.args.train_val_ratio)
		split_idx = int(len(vf_img_list)*.6)
		split_idx_val = int(len(vf_img_list)*.2)
		target_img_list.extend(vf_img_list[:split_idx]*10) if self.is_Train else target_img_list.extend(vf_img_list[split_idx:split_idx+split_idx_val]) 
		vf_len = len(target_img_list)

		# split_idx = int(len(nonvf_img_list)*self.args.train_val_ratio)
		split_idx = int(len(nonvf_img_list)*.6)
		split_idx_val = int(len(nonvf_img_list)*.2)
		target_img_list.extend(nonvf_img_list[:split_idx]) if self.is_Train else target_img_list.extend(nonvf_img_list[split_idx:split_idx+split_idx_val]) 
		nonvf_len = len(target_img_list) -vf_len

		target_label_list = ([1] * vf_len)+([0] * nonvf_len)
		print("VF",vf_len,"Non-VF",nonvf_len)
		return target_img_list,target_label_list

	def remove_invalid_fracture(self,imglist):
		new_nvf_lilist = []
		new_vf_lilist = []
		for img in imglist:
			# Exclude no evaluable images
			if "_".join(img.split("/")[-2:]) in excluded_list:
				continue

			image = sitk.ReadImage(img)
			image = sitk.GetArrayFromImage(image)[0]
			if len(image.shape) != 2:
				continue
			y,x =  image.shape

			if y < 500 or x < 500:
				continue
			else:
				if "_".join(img.split("/")[-2:]) in fracturelist:
					new_vf_lilist.append(img)
				else:
					new_nvf_lilist.append(img)

		return new_vf_lilist,new_nvf_lilist

	def remove_invalid(self,imglist):
		new_lilist = []
		for img in imglist:
			# Exclude no evaluable images
			if "_".join(img.split("/")[-2:]) in excluded_list:
				continue

			image = sitk.ReadImage(img)
			image = sitk.GetArrayFromImage(image)[0]
			if len(image.shape) != 2:
				continue
			y,x =  image.shape

			if y < 500 or x < 500:
				continue
			else:
				new_lilist.append(img)
		
		return new_lilist


	def augment_img(self, img):
		if self.args.augmentation == 'heavy':
			scale_factor = random.uniform(1-self.args.scale_factor, 1+self.args.scale_factor)
			rot_factor = random.uniform(-self.args.rot_factor, self.args.rot_factor)

			seq = iaa.Sequential([
					iaa.Affine(
						scale=(scale_factor, scale_factor),
						rotate=rot_factor,
						translate_percent={"x": (-0.1, 0.1), "y": (-0.1, 0.1)},
					),
					iaa.Sometimes(
						0.5,
						iaa.GaussianBlur(sigma=(0, 1.0))
					),
					iaa.Crop(percent=(0, 0.05)),
				], random_order=True)

		else:
			scale_factor = random.uniform(1-self.args.scale_factor, 1+self.args.scale_factor)
			rot_factor = random.uniform(-self.args.rot_factor, self.args.rot_factor)

			seq = iaa.Sequential([
					iaa.Affine(
						scale=(scale_factor, scale_factor),
						rotate=rot_factor
					)
				], random_order=True)

		seq_det = seq.to_deterministic()

		if np.ndim(img) == 2:
			# Grayscale
			img = seq_det.augment_images(img)
		elif np.ndim(img) == 3:
			# (C, H, W) -> (1, H, W, C)
			img = np.moveaxis(img, 0, -1)[None, ...]
			img = seq_det.augment_images(img)
			img = np.moveaxis(img[0], -1, 0)

		return img

	def save_imglist(self):
		# save and compress.
		with gzip.open("exp/valid_list.pkl", 'wb') as f:
			pickle.dump(self.img_list, f)

		return

def load_dataloader(args):
	tr_set = VFDataset(is_Train=True, args=args)
	val_set = VFDataset(is_Train=False, args=args)

	batch_train = DataLoader(tr_set, batch_size=args.batch_size, shuffle=True, num_workers=28, pin_memory=True)
	batch_val = DataLoader(val_set, batch_size=args.batch_size, shuffle=False, num_workers=28, pin_memory=True)

	return batch_train, batch_val
